    THEN
        IF process IS NOT NULL and process <> 'Storage' THEN
            fail_flag := fail_flag || process || ' ';
        END IF;
        process := 'Storage';
    END IF;

    -- Filtration
    IF object_name = 'filter' OR -- think this should be a LIKE cos there is 'bio_filter'
