        END IF;
        asset := 'Generator';
    END IF;

    -- Spare
    IF point_name LIKE '%spare%' THEN
        IF asset IS NOT NULL THEN
            overwritten := overwritten || asset || ' ';
        END IF;
        address_type = SUBSTRING(db_addr, 1, 1);
        IF address_type = 'C' THEN
            asset := 'Character';
        ELSEIF address_type = 'B' THEN
