        ELSE
            pump_num = (SPLIT_PART(pump_num_part, ' ', 2));
            IF pump_num ~ '^[0-9]+$' THEN
                asset := ('Valve ' || pump_num);
            ELSE
                asset := 'Valve';
            END IF;
        END IF;
    END IF;


    -- Unknown
    IF asset IS NULL THEN
        asset := 'Unknown';
    END IF;

    RETURN NEXT;
END
$$;
