            asset := 'Sump';
        END IF;
    END IF;

    -- Flow
    -- todo - if dbname contains word flow or acronyms & object_name IS NOT analog_value, asset = Flow -- so the same thing then.. ?
    IF (point_name LIKE '%flow%' OR
        point_name LIKE '%flw%')
            AND
