                IF LOWER(part) IN ('stw', 'sbr', 'sps', 'plc', 'rsps', 'gbt', 'ps', 'wtw', 'edm', 'res', 'abp', 'rtu',
                                   'cp', 'azp', 'cso', 'os', 'it', 'drv', 'est', 'pl', 'in', 'uhf', 'wrc', 'prv', 'dbn',
                                   'opp', 'fm') OR LOWER(part) LIKE ('plc%') THEN
