    END IF;

    -- Site
    -- todo - pass in process to create_asset
    IF point_name LIKE '%rtu%' AND process = 'site' THEN
        IF asset IS NOT NULL THEN
            overwritten := overwritten || asset || ' ';
        END IF;
        asset := 'RTU';
    END IF;
    IF point_name LIKE '%plc%' AND process = 'site' THEN
