    END IF;
    IF point_name LIKE '%vent stack%' THEN
        IF asset IS NOT NULL THEN
            overwritten := overwritten || asset || ' ';
        END IF;
        asset := 'Vent Stack';
    END IF;
    IF point_name LIKE '%intake vent fan%' THEN
        IF asset IS NOT NULL THEN
