        mimic_name,
        object_name,
        object_instance,

        server_name,
        db_addr,
        point_name,
        above_works,
        works,
        (process_result).process,
        function_result.function,
    -- dno why this is complaining when it literally works :O
        asset_result.asset,
        (process_result).fail_flag AS process_overwritten,
        function_result.fail_flag AS function_overwritten,
